/// <reference path="types/MicrosoftMaps/Microsoft.Maps.All.d.ts" />
/// <reference path="interfaces/BingMapsInterfaces.ts" />

let preciseBingMap: PreciseBingMap;
let circleBingMap: CircleBingMap;

class PreciseBingMap {
    map: Microsoft.Maps.Map;

    constructor(lat, long) {

        window.scroll({
            top: 0,
            left: 0,
            behavior: 'smooth'
        });

        this.map = new Microsoft.Maps.Map('#myMap', {
            center: new Microsoft.Maps.Location(lat, long),
            mapTypeId: Microsoft.Maps.MapTypeId.road,
            zoom: 18
        });

        let pushpin = new Microsoft.Maps.Pushpin(new Microsoft.Maps.Location(lat, long), {
            icon: '<svg xmlns:svg="http://www.w3.org/2000/svg" xmlns="http://www.w3.org/2000/svg" xmlns:xlink="http://www.w3.org/1999/xlink" width="25" height="40"><circle cx="12.5" cy="14.5" r="10" fill="{color}"/><image x="0" y="0" height="40" width="25" xlink:href="data:image/png;base64,"/></svg>',
            anchor: new Microsoft.Maps.Point(12, 39),
            color: "red"
        });

        this.map?.entities.push(pushpin);

        window.scroll({
            top: 0,
            left: 0,
            behavior: 'smooth'
        });
    }
}

class CircleBingMap {
    map: Microsoft.Maps.Map;

    constructor(lat, long, sitePolygon: bingPolygon) {
        window.scroll({
            top: 0,
            left: 0,
            behavior: 'smooth'
        });
        this.map = new Microsoft.Maps.Map('#myMap', {
            center: new Microsoft.Maps.Location(lat, long),
            mapTypeId: Microsoft.Maps.MapTypeId.road,
            zoom: 18
        });
        let polygon = new Microsoft.Maps.Polygon(sitePolygon.rings, { strokeColor : "red", strokeThickness : 2 });
        this.map?.entities.push(polygon);
        window.scroll({
            top: 0,
            left: 0,
            behavior: 'smooth'
        });
    }
}

function loadCircleMap(lat, long, sitePolygon): void {
    circleBingMap = new CircleBingMap(lat, long, JSON.parse(sitePolygon));
}

function loadPreciseMap(lat, long): void {
    preciseBingMap = new PreciseBingMap(lat, long);
}

